function wait(ms) {
    return new Promise((resolve) => {
      setTimeout(resolve, ms);
    });
  }

  function testDuree(duration) {
    const regex = /^(\d+)(ms|s|m|h|d)?$/;

    const match = duration.match(regex);

    if (!match) {
        return null; 
    }

    const value = parseInt(match[1], 10);
    const unit = match[2] || 'ms';

    switch (unit) {
        case 'ms':
            return `${value}ms`;
        case 's':
            return `${value}s`;
        case 'm':
            return `${value}m`;
        case 'h':
            return `${value}h`;
        case 'd':
            return `${value}d`;
        default:
            return null; 
    }
}

